async function getLayoutDisplay(file,z){
    let x = await fetch("./layout/"+file+".html");
    let y = await x.text();
    document.getElementById(z).innerHTML = y;
}

getLayoutDisplay('navbar','nav-area');
getLayoutDisplay('offcanvas','offcanvas-area');
getLayoutDisplay('sidebar','sidebar-area');
getLayoutDisplay('main','main-area');
